import { useState } from "react";
import './counter.css'
export default function Counter() {
  // let count = 0

  // const handlePlus = () => {
  //   count++
  //   console.log(count);
  // }

  // –º—ã –≤–∑—ã–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é useState()
  // –ø–µ—Ä–µ–¥–∞–µ–º –≤ —Å–∫–æ–±–∫–∞—Ö –Ω–∞—á–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ –¥–ª—è –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π
  // –≤ –æ—Ç–≤–µ—Ç –º—ã –ø–æ–ª—É—á–∞–µ–º –º–∞—Å—Å–∏–≤ –∏–∑ –¥–≤—É—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤
  // –ø–µ—Ä–≤—ã–π - —Å–∞–º–∞ –≤–æ–ª—à–µ–±–Ω–∞—è –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è, –≤—ã–∑—ã–≤–∞—é—â–∞—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–Ω–∞ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏
  // –≤—Ç–æ—Ä–æ–π - —Ñ—É–Ω–∫—Ü–∏—è, –ø–µ—Ä–µ–∑–∞–ø–∏—Å—ã–≤–∞—é—â–∞—è –∑–Ω–∞—á–µ–Ω–∏–µ —ç—Ç–æ–π –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π
  // –∏–∑–º–µ–Ω—è—Ç—å –ø–µ—Ä–µ–º–µ–Ω–Ω—É—é –º—ã –±—É–¥–µ–º —Ç–æ–ª—å–∫–æ —Å –ø–æ–º–æ—â—å—é —ç—Ç–æ–π —Ñ—É–Ω–∫—Ü–∏–∏

  // –≤ –æ—Ç–≤–µ—Ç –Ω–∞ –≤—ã–∑–æ–≤ —Ñ—É–Ω–∫—Ü–∏–∏ useState() —Å –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–º –Ω–∞—á–∞–ª—å–Ω—ã–º –∑–Ω–∞—á–µ–Ω–∏–µ–º –¥–ª—è –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π –ø—Ä–∏—Ö–æ–¥–∏—Ç –º–∞—Å—Å–∏–≤ –∏–∑ –¥–≤—É—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤: –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è —Å —Å–æ—Å—Ç–æ—è–Ω–∏–µ–º —Å —ç—Ç–∏–º –Ω–∞—á–∞–ª—å–Ω—ã–º –∑–Ω–∞—á–µ–Ω–∏–µ–º –∏ —Ñ—É–Ω–∫—Ü–∏—è –µ–µ –∏–∑–º–µ–Ω—è—é—â–∞—è

  // const state = useState(0);
  // const state1 = useState("students");
  // const state2 = useState([1, 2, 3]);

  // // –≤–æ—Ç —á—Ç–æ –Ω–∞–º –ø—Ä–∏—à–ª–æ –≤ –æ—Ç–≤–µ—Ç:

  // console.log(state);
  // console.log(state1);
  // console.log(state2);

  // // —Ç–∞–∫ –º—ã –∑–∞–±—Ä–∞–ª–∏ –∑–Ω–∞—á–µ–Ω–∏—è –≤ –ø–µ—Ä–µ–º–µ–Ω–Ω—É—é
  // let arr = state2[0];
  // let setArr = state2[1];

  // console.log(arr);
  // console.log(setArr);

  const [count, setCount] = useState(0);

  const handlePlus = () => {
    setCount(prev => prev + 1);
  };

  const handleMinus = () => {
    setCount(prev => prev - 1);
  };

  return (
    <div className="lesson-container">
      <h4>Counter</h4>
      <div>
        <button onClick={handleMinus}>-</button>
        <span className='counterValue'>{count}</span>
        <button onClick={handlePlus}>+</button>
      </div>
      <p>
        –ú—ã –Ω–µ –º–æ–∂–µ–º —Ö—Ä–∞–Ω–∏—Ç—å –∏–∑–º–µ–Ω—è–µ–º—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ react –≤ –∫–ª–∞—Å—Å–∏—á–µ—Å–∫–∏—Ö –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö, –ø–æ—Ç–æ–º—É —á—Ç–æ –º—ã –Ω–µ
        –±—É–¥–µ–º –≤–∏–¥–µ—Ç—å –∏—Ö –∏–∑–º–µ–Ω–µ–Ω–∏—è –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –Ω–µ –±—É–¥–µ—Ç –æ–±–Ω–æ–≤–ª—è—Ç—å—Å—è üò¢
      </p>
      <p>
        –ú–Ω–µ –Ω—É–∂–Ω–∞ '—Ö–∏—Ç—Ä–∞—è' –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è, –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –∫–æ—Ç–æ—Ä–æ–π –±—É–¥–µ—Ç –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç—å –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
      </p>
      <p>–í –∫–∞–∫–∏—Ö –∂–µ –∫–∞–∫–∏—Ö —Å–ª—É—á–∞—è –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö? üßê</p>
      <ol>
        <li>–ò–∑–º–µ–Ω–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è (state)</li>
        <li>–ò–∑–º–µ–Ω–µ–Ω–∏–µ props</li>
      </ol>
      <p>
        –°–æ—Å—Ç–æ—è–Ω–∏–µ –∏–ª–∏ state - —ç—Ç–æ –æ—Å–æ–±–∞—è –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è —Å –¥–∞–Ω–Ω—ã–º–∏ –≤ react –∏–∑–º–µ–Ω–µ–Ω–∏–µ –∫–æ—Ç–æ—Ä–æ–π –≤—ã–∑—ã–≤–∞–µ—Ç
        –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
      </p>
      <p>
        –î–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è –º—ã –∏—Å–ø–æ–ª—å–∑—É–µ–º –≤—Å—Ç—Ä–æ–µ–Ω–Ω—É—é –≤ react —Ñ—É–Ω–∫—Ü–∏—é useState(). –¢–∞–∫–∏–µ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–∏–µ
        —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ –Ω–∞–∑—ã–≤–∞–µ—Ç—Å—è —Ö—É–∫–∞–º–∏ ü™ù –û–Ω–∏ —Ä–µ—à–∞–µ—Ç —Ä–∞–∑–Ω—ã–µ –∑–∞–¥–∞—á–∏, –Ω–æ —ç—Ç–æ –≤—Å–µ–≥–¥–∞ —Å–∞–º—ã–µ
        –ø–æ–ª–µ–∑–Ω—ã–µ –∏ —á–∞—Å—Ç–æ –≤—Å—Ç—Ä–µ—á–∞—é—â–∏–µ—Å—è –∑–∞–¥–∞—á–∏. –ù–∞–ø—Ä–∏–º–µ—Ä, —Ö—Ä–∞–Ω–µ–Ω–∏–µ –∏–∑–º–µ–Ω—è–µ–º—ã—Ö –¥–∞–Ω–Ω—ã—Ö
      </p>
    </div>
  );
}
