import "./lesson04.css";

import { forceUsers } from "./data";
export default function Lesson04() {
  // console.log(forceUsers);
  return (
    <div className="lesson-container">
    <header className="lesson-header">
      <h3>Lesson 04</h3>
    </header>

      {/* –∏—Ç–µ—Ä–∏—Ä—É–µ–º—Å—è –ø–æ –º–∞—Å—Å–∏–≤—É –æ–±—ä–µ–∫—Ç–æ–≤ –∏ –≤—ã–≤–æ–¥–∏–º –¥–∞–Ω–Ω—ã–µ –ø–æ –∫–ª—é—á–∞–º */}
      {forceUsers.map((hero, index) => (
        // –¥–ª—è –æ–±–µ—Ä—Ç–∫–∏ –Ω–∞–¥ –∏—Ç–µ—Ä–∏—Ä—É–µ–º–æ–π –≤–µ—Ä—Å—Ç–∫–æ–π —É–∫–∞–∑—ã–≤–∞–µ–º props key —Å 
        // —É–Ω–∏–∫–∞–ª—å–Ω—ã–º –∑–Ω–∞—á–µ–Ω–∏–µ–º. —ç—Ç–æ —Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∏–µ –¥–∞–Ω–Ω—ã–µ, –∫–æ—Ç–æ—Ä—ã–π –ø—Ä–æ—Å–∏—Ç react
        <div key={index} className={`card ${hero.isDark ? 'isDark' : 'isLight'}`}>
          <h4>{hero.name}</h4>
          <p>Age: {hero.age}</p>
          <img width={200} src={hero.image} alt={hero.name} />
          
          <p>
            Lightsaber colors:
            <div className="lightsaber-colors">
              {hero.lightsaberColors.map((color, idx) => (
                <span key={idx} style={{ backgroundColor: color }}></span>
              // üîê <div style={{ backgroundColor: color, height: "10px", width: "10px" }}></div>
            ))}
            </div>
          </p>
        </div>
      ))}
    </div>
  );
}
